import '../Style/Moves.css'
import {useState} from 'react'
import MoviesLogic from './moviesLogic'
import { useEffect } from 'react'


const Moves = () => {
    let [favratesData, setfavData] = useState([])
    useEffect(()=>{ 
   let  fetchData=async()=>{
        let response= await fetch('http://localhost:4005/favrates')
        let data= await response.json()
        setfavData(data)
    }
    fetchData()
},[])

    let handleRemove = (id) => {
        setfavData(favratesData.filter(x => x.id !== id))
        alert(`${id} has been removed`)
    }
    return ( 
        <div className="moves">
            <MoviesLogic title="Favraties of all time" data={favratesData} fn={handleRemove}/>
            <MoviesLogic title="Top rated" data={favratesData.filter(x=>x.rating>4.5)} fn={handleRemove}/>
            <MoviesLogic title="Top english movies" data={favratesData.filter(x=>x.language == "english")} fn={handleRemove}/>
        </div>
     );
}

export default Moves;
