import { useState } from "react";
import '../Style/addbook.css'
import { useNavigate } from "react-router-dom";

const AddBook  = () => {
    let [title,setTitle]=useState([])
    let [authors,setAuthors]=useState([])
    let [categories,setCategories]=useState([])
    // let [shortDescription,setShortDescription]=useState([])
    let [longDescription,setLongDescription]=useState([])
    let [thumbnailUrl,setThum]=useState([])
    let [pageCount,setPageCount]=useState([])

    let navigate=useNavigate()

    let handleSubmit=(e)=>{
        e.preventDefault()

         
        let data={title,authors,categories,longDescription,thumbnailUrl,pageCount}
        fetch(`http://localhost:2002/books`,{
            mthod:'POST',
            headers:{'Content-Type':'application-json'},
            body:JSON.stringify(data)
        })
        alert('book added to server')
        navigate(`/admin/book-list`)
    }

    return ( 
        <div className="addBooks">
        <h1>Add a Book</h1>
        <div className="form">
            <form action="" onSubmit={handleSubmit}>
                 <div className="title">
                   <label htmlFor="">title <input type="text" required placeholder="enter the title" value={title} onChange={(e)=>setTitle(e.target.value)}/></label>
                 </div>
                 <div className="authorss">
                 <label htmlFor="">authorss <input type="text" required placeholder="enter the authorss" value={authors} onChange={(e)=>setAuthors(e.target.value)}/></label>
                 </div>
                 <div className="categories">
                 <label htmlFor="">categories <input type="text" required placeholder="enter the categories" value={categories} onChange={(e)=>setCategories(e.target.value)}/></label>
                 </div>
                 <div className="pageCount">
                 <label htmlFor="">pageCount <input type="text" required placeholder="enter the pageCount" value={pageCount} onChange={(e)=>setPageCount(e.target.value)}/></label>
                 </div>
                 {/* <div className="shortDescription">
                 <label htmlFor="">shortDescription <textarea cols="30" rows="10" required placeholder="enter the shortdesc" value={shortDescription}  onChange={(e)=>setShortDescription(e.target.value)} ></textarea></label>
                 </div> */}
                 <div className="longDescription">
                 <label htmlFor="">longDescription  <textarea cols="30" rows="10 " required placeholder="enter the longdesc" value={longDescription} onChange={(e)=>setLongDescription(e.target.value)}></textarea> onChange </label>
                 </div>
                 <div className="thumbnailUrlbnailUrl">
                 <label htmlFor="">thumbnailUrlbnailUrl <input type="text" required placeholder="enter the thumbnailUrlbnailurl" value={thumbnailUrl} onChange={(e)=>setThum(e.target.value)}/></label>
                 </div>
                 <button>addbook</button>
                 <button>Reset</button>
            </form>
        </div>
    </div>
     );
}
 
export default AddBook ;
