import { useState } from 'react'
import {useNavigate} from "react-router-dom"
import '../Style/adduser.css'
const AddUser = () => {
    let [name,setName]=useState('')
    let [age,setAge]=useState('')
    let [email,setMail]=useState('')
    let [phonenumber,setPhonenumber]=useState('')
    let navigate = useNavigate()

    let handleSubmit=(e)=>{
       e.preventDefault()

       let data={name,age,email,phonenumber}
       fetch('http://localhost:2002/users',{
        method:'POST',
        headers:{'Content-Type':'application/json'},
        body:JSON.stringify(data)
    })
    alert('data added to server')
    navigate("/admin/user-list")
    }
    let reset=()=>{
        setName("")
        setAge("")
        setMail("")
        setPhonenumber("")
    }

    return (
        <div className="addfavrates">

            <div className="form">
                <div className="inputs">
                    <form action="" onSubmit={handleSubmit}>
                        <div className="name">
                            <input type="text" placeholder="enter name" value={name} onChange={(e)=>setName(e.target.value)} />
                        </div>
                        <div className="age">
                            <input type="text" placeholder="enter the age"  value={age} onChange={(e)=>setAge(e.target.value)}/>
                        </div>
                        <div className="mail">
                            <input type="email" placeholder="enter the mail" value={email} onChange={(e)=>setMail(e.target.value)} />
                        </div>
                        <div className="phonenumber">
                            <input type="tel" placeholder="enter the phonenumber" value={phonenumber} onChange={(e)=>setPhonenumber(e.target.value)} />
                        </div>
                        <button type='submit'>Add to User</button>
                        <button type='reset' onClick={reset} >Reset</button>
                    </form>
                </div>
            </div>
        </div>

    );
}

export default AddUser;
