import { useState } from "react";
import '../Style/addbook.css'

const AddBook  = () => {
    let [title,setTitle]=useState([])
    let [author,setAuthor]=useState([])
    let [categories,setCategories]=useState([])
    let [sdes,setSdes]=useState([])
    let [ldes,setLdes]=useState([])
    let [thum,setThum]=useState([])
    let [pcount,setPcount]=useState([])

    let handleSubmit=(e)=>{
        e.preventDefault()

         
        let data={title,author,categories,sdes,ldes,thum,pcount}
        fetch(``)
    }

    return ( 
        <div className="addBooks">
        <h1>Add a Book</h1>
        <div className="form">
            <form action="" onSubmit={handleSubmit}>
                 <div className="title">
                   <label htmlFor="">title <input type="text" required placeholder="enter the title" value={title} onChange={(e)=>setTitle(e.target.value)}/></label>
                 </div>
                 <div className="authors">
                 <label htmlFor="">authors <input type="text" required placeholder="enter the authors" value={author} onChange={(e)=>setAuthor(e.target.value)}/></label>
                 </div>
                 <div className="categories">
                 <label htmlFor="">categories <input type="text" required placeholder="enter the categories" value={categories} onChange={(e)=>setCategories(e.target.value)}/></label>
                 </div>
                 <div className="pageCount">
                 <label htmlFor="">pageCount <input type="text" required placeholder="enter the pageCount" value={pcount} onChange={(e)=>setPcount(e.target.value)}/></label>
                 </div>
                 <div className="shortDescription">
                 <label htmlFor="">shortDescription <textarea cols="30" rows="10" required placeholder="enter the shortdesc" value={sdes}  onChange={(e)=>setSdes(e.target.value)} ></textarea></label>
                 </div>
                 <div className="longDescription">
                 <label htmlFor="">longDescription  <textarea cols="30" rows="10 " required placeholder="enter the longdesc" value={ldes} onChange={(e)=>setLdes(e.target.value)}></textarea> onChange </label>
                 </div>
                 <div className="thumbnailUrl">
                 <label htmlFor="">thumbnailUrl <input type="text" required placeholder="enter the thumbnailurl" value={thum} onChange={(e)=>setThum(e.target.value)}/></label>
                 </div>
                 <button>addbook</button>
                 <button>Reset</button>
            </form>
        </div>
    </div>
     );
}
 
export default AddBook ;
