    useEffect(()=>{
        let fetchData=async()=>{
            let response=await fetch(`http://localhost:2002/books/${id}`)
            let data=await response.json()
            setBooks(data)
        }
        fetchData()
    },[books])

    //Delete from server
    let handleDelete=(id,title)=>{
        fetch(`http://localhost:2002/books/${id}`,{
         method:'DELETE'
        })
        alert(`${title} will be deleted permanently`)
    }

    //Deleting not permanently
    // let handleDelete=(id,title)=>{
    //     setBooks(books.filter(x=>x.id!=id))
    //     alert(`${title} has been deleted`)
    // }
    return ( 
        <div className="booklist">
            <h1>Book List: {books.length}</h1>
            <div className="book_section">
                {books.map((data)=>(
                   <div className="bookCard">
                   <div className="bookCard_img"> 
                   <img height={250} width={250} src={data.thumbnailUrl} alt="" />
                   </div>
                    <div className="bookCard_detail">
                    <h4 style={{color:"darkblue"}}>Title: {data.title}</h4> 
                    <h6>Authors: {data.authors.toString()}</h6>
                    <h6>Pagecount: {data.pageCount}</h6>
