                    <button onClick={()=>readBook(data.id,data.title)}>Readmore</button>
                    <button onClick={()=>handleDelete(data.id,data.title)}>Delete</button>
                    </div>
                    <ReadBooks data={readBook}/>
                    </div>
                   </div> 
                ))}
            </div>
        </div>
     );
}
 
export default BookList;
