import '../Style/Moves.css'
import {useState} from 'react'
import MoviesLogic from './moviesLogic'


const Moves = () => {
    let favrates=[
        {
            title:"Mirzapur ",
            genre:"thriller ",
            language:"english",
            rating:4.5,
            id:1
        },
        {
            title:"Dark",
            genre:"horror",
            language:"english",
            rating:4,
            id:2
        },
        {
            title:"Family man",
            genre:"thriller",
            language:"hindi",
            rating:4.5,
            id:3
        },
        {
            title:"Malgudi Days",
            genre:"philosophy",
            language:"hindi",
            rating:4.9,
            id:4
        },
        {
            title:"Moneyheast",
            genre:"thriller",
            language:"hindi",
            rating:4.8,
            id:5
        }
    ]
    let [favratesData, setfavData] = useState(favrates)
    let handleRemove = (id) => {
        setfavData(favratesData.filter(x => x.id !== id))
        alert(`${id} has been removed`)
    }
    return ( 
        <div className="moves">
            <MoviesLogic title="Favraties of all time" data={favratesData} fn={handleRemove}/>
       {/* <MoviesLogic title="Favraties of all time" data={favrates} fn={handleRemove}/> */}
       <MoviesLogic title="Favraties of all time" data={favratesData} fn={handleRemove}/>
        </div>
     );
}

export default Moves;
