import { useState } from "react";
import { useEffect } from "react";

const Movies = () => {
    let [moviesData,setData]=useState([])
    useEffect(()=>{
        let fetchData=async()=>{
            let response= await fetch('http://localhost:4000/Movies ')
            let data= await response.json()
            moviesData(data)
        }
        fetchData()
    },[])
    let handle=(imdbID)=>{
        setData(moviesData.filter(x=>x.imdbID !== imdbID))
        alert(`${imdbId} has been removed`)
    }
    
    return ( 
        <div className="movies">
          <Movies data={moviesData} data1={handle}/>
        </div>

     );
}
 
export default Movies;


















