import '../Style/Moves.css'
import {useState} from 'react'
const Moves = () => {
    let favrates=[
        {
            title:"Mirzapur ",
            genre:"thriller ",
            language:"english",
            rating:4.5,
            id:1
        },
        {
            title:"Dark",
            genre:"horror",
            language:"english",
            rating:4,
            id:2
        },
        {
            title:"Family man",
            genre:"thriller",
            language:"hindi",
            rating:4.5,
            id:3
        },
        {
            title:"Malgudi Days",
            genre:"philosophy",
            language:"hindi",
            rating:4.9,
            id:4
        },
        {
            title:"Moneyheast",
            genre:"thriller",
            language:"hindi",
            rating:4.8,
            id:5
        }
    ]
    let [favratesData, setfavData] = useState(favrates)
    let handleRemove = (id,genre) => {
        setfavData(favratesData.filter(x => x.id != id))
        alert(`${id} has been removed ${genre} has been removed`)
    }
    return ( 
        <div className="moves">
        <h1>Favraties of all time</h1>
        <div className="fav">
            {favrates.map((data)=>(
             <div className="favratelist">
                <h2>{data.title}</h2>
                <p>{data.language}/{data.genre}</p>
                <p>{data.rating}⭐</p>
                <p>{data.id}</p>
                <button onClick={() => handleRemove(data.id, data.genre)} >Remove</button>
             </div>
            ))}
        </div>
        </div>
     );
}

export default Moves;


