import { useState } from "react";
import { useEffect } from "react";

const Movies = () => {
    let [moviesData,setData]=useState([])
    useEffect(()=>{
        let fetchData=async()=>{
            let response= await fetch('http://localhost:4000/Movies ')
            let data= await response.json()
            moviesData(data)
        }
        fetchData()
    },[])

    return ( 
        <div className="movies">
            <h1>{Title}</h1>
            <h2>{Genre}</h2>
            <h2>{Rated}</h2>
            <h2>{Director}</h2>
            <h2>{Plot}</h2>
            <div className="btn">
                <button onClick={()=>handle(data.imdbID !== imdbID)}>Remove</button>
            </div>
        </div>

     );
}
 
export default Movies;


















