import { useState } from "react";
import '../Style/Celebraties.css'

const Celebs = () => {
    //database
    let stars = [
        {
            img: "images/img7.jpg",
            name: "Anu",
            id: "01"
        },
        {
            img: "images/img8.jpg",
            name: "Ashika",
            id: "02"
        },
        {
            img: "images/img9.jpg",
            name: "Jhanvi",
            id: "03"
        },
        {
            img: "images/img10.jpg",
            name: "Mallika",
            id: "04"
        },
        {
            img: "images/img11.jpg",
            name: "Random",
            id: "05"
        }

    ]
    let [starsData, setData] = useState(stars)
    let handleRemove = (id, name) => {
        setData(starsData.filter(x => x.id != id))
        alert(`${name} has been removed`)
    }
    return (
        <div className="celebs">
            <h1>Top Celebrities</h1>
            {starsData.map(data => (
                <div className="celebs_list">
                    <div className="images">
                        <img src={data.img} alt="" />
                    </div>
                    <div className="info">
                        <h1>{data.name}</h1>
                        <button onClick={() => handleRemove(data.id, data.name)}>Remove</button>
                    </div>
                </div>

            ))}
        </div>
    );
}

export default Celebs;
