import { useState } from "react";

const AddBook  = () => {
    let [title,setTitle]=useState([])
    let [author,setAuthor]=useState([])
    let [categories,setCategories]=useState([])
    let [sdes,setSdes]=useState([])
    let [ldes,setLdes]=useState([])
    let [thum,setThum]=useState([])
    let [pcount,setPcount]=useState([])

    let handleSubmit=(e)=>{
        e.preventDefault()
         
    }

    return ( 
        <div className="addBooks">
        <h1>Add a Book</h1>
        <div className="form">
            <form action="" onSubmit={handleSubmit}>
                 <div className="title">
                   <label htmlFor="">title <input type="text" required placeholder="enter the title" value={title} onChange={()=>setTitle(e.target.value)}/></label>
                 </div>
                 <div className="authors">
                 <label htmlFor="">authors <input type="text" required placeholder="enter the authors" value={author} onChange={()=>setAuthor(e.target.value)}/></label>
                 </div>
                 <div className="categories">
                 <label htmlFor="">categories <input type="text" required placeholder="enter the categories" value={categories} onChange={()=>setCategories(e.target.value)}/></label>
                 </div>
                 <div className="pageCount">
                 <label htmlFor="">pageCount <input type="text" required placeholder="enter the pageCount" value={pcount} onChange={()=>setPcount(e.target.value)}/></label>
                 </div>
                 <div className="shortDescription">
                 <label htmlFor="">shortDescription <textarea cols="30" rows="10" value={sdes}  onChange={()=>setSdes(e.target.value)} ></textarea></label>
                 </div>
                 <div className="longDescription">
                 <label htmlFor="">longDescription  <textarea cols="30" rows="10 " value={ldes} onChange={()=>setLdes(e.target.value)}></textarea> onChange </label>
                 </div>
                 <div className="thumbnailUrl">
                 <label htmlFor="">thumbnailUrl <input type="text" required placeholder="enter the thumbnailurl" value={thum} onChange={()=>setThum(e.target.value)}/></label>
                 </div>
                 <button>addbook</button>
                 <button>Reset</button>
            </form>
        </div>
    </div>
     );
}
 
export default AddBook ;
