import { useState } from "react";

const Celebraties = () => {
        let stars=[{
            img:"images/img7.jpg",
            name:"Anu-Emmenueal",
            id:"01"
        },
        {
            img:"images/img8.jpg",
            name:"Ashika",
            id:"02"
        },
        {
            img:"images/img9.jpg",
            name:"Jhanvi",
            id:"03"
        },
        {
            img:"images/img10.jpg",
            name:"Mallika-shingh",
            id:"04"
        },
        {
            img:"images/img11.jpg",
            name:"Random",
            id:"05"
        },
    ]
    let [starsdata,setdata]=useState(stars)
    let handleremove=(id,name)=>{
        setdata (starsdata.filter(x=>x.id !=id))
        alert(`${name} hasbeen removed`)
    }
    
    return ( 
    <div className="celeb">
        <h1>TOP CELEBRATIES</h1>
        { starsdata.map=(data)=>(
             <div className="celebratieslist">
            <div className="image">
                <img src={data.image} alt="" />
            </div>
            <div className="info">
                <h1>{data.name}</h1>
                <button onClick={()=>handleremove(data.id,data.name)} >Remove</button>
            </div>
        </div>
            )}
    </div>

     );

    } 
export default Celebraties;
