import '../Style/Homepage.css'
import MoviesLogic from './moviesLogic';

const Homepage = () => {
    let img=[{
        img:'images/img1.jpg'},
       { img:'images/img2.jpg'},
       { img:'images/img3.jpg'},
       { img:'images/img4.jpg'},
       { img:'images/img5.jpg'}]

    return (
        <div className="banner">
            <div className="bannerimage">
            <div className="para">
                <p>Lorem ipsum dolor sit amet, consectetur  adipisicing elit.</p>
                <button>Subscribe now</button>
            </div>
                <div className="img1">
            <img src="https://img1.hotstarext.com/image/upload/f_auto,t_web_m_1x/sources/r1/cms/prod/9476/1429476-h-fb7ceee122b4" width={1050} height={500} alt="" />
            </div>
            
            </div>
            <div className="movieslist">
                <h1>Movies of the year</h1>
                {
                img.map((data)=>(
                    <img src={data.img} alt="" />
                ))
            }
                
            </div>  
            </div>

    );
}

export default Homepage;



