import { useState } from 'react'
import {useNavigate} from "react-router-dom"
const AddUser = () => {
    let [name,setName]=useState('')
    let [age,setAge]=useState('')
    let [mail,setMail]=useState('')
    let [phonenumber,setPhonenumber]=useState('')
    let navigate = useNavigate()

    let handleSubmit=(e)=>{
       e.preventDefault()

       let data={title,genre,language,rating}
       fetch('http://localhost:2002/users',{
        method:'POST',
        headers:{'Content-Type':'application/json'},
        body:JSON.stringify(data)
    })
    alert('data added to server')
    navigate("/admin/user-list")
    }
    let reset=()=>{
        setName("")
        setAge("")
        setMail("")
        setPhonenumber("")
    }

    return (
        <div className="addfavrates">

            <div className="form">
                <div className="inputs">
                    <form action="" onSubmit={handleSubmit}>
                        <div className="name">
                            <input type="text" placeholder="enter title" value={name} onChange={(e)=>setTitle(e.target.value)} />
                        </div>
                        <div className="age">
                            <input type="text" placeholder="enter the genre of the movie"  value={age} onChange={(e)=>setGenre(e.target.value)}/>
                        </div>
                        <div className="mail">
                            <input type="text" placeholder="enter the language" value={mail} onChange={(e)=>setLanguage(e.target.value)} />
                        </div>
                        <div className="phonenumber">
                            <input type="text" placeholder="enter the rating" value={phonenumber} onChange={(e)=>setRating(e.target.value)} />
                        </div>
                        <button type='submit'>Add to User</button>
                        <button type='reset' onClick={reset} >Reset</button>
                    </form>
                </div>
            </div>
        </div>

    );
}

export default AddUser;
