import { useState,useEffect } from "react";
import'../styles/bookList.css'

const BookList = () => {
    let [books,setBooks]=useState([])

    useEffect(()=>{
        let fetchData=async()=>{
            let response=await fetch('http://localhost:2002/books')
            let data=await response.json()
            setBooks(data)
        }
        fetchData()
    },[])
    
    let handleDelete=(id,title)=>{
        setBooks(books.filter(x=>x.id!=id))
        alert(`${title} has been deleted`)
    }
    return ( 
        <div className="booklist">
            <h1>Book List: {books.length}</h1>
            <div className="book_section">
                {books.map((data)=>(
                   <div className="bookCard">
                   <div className="bookCard_img"> <img src={data.thumbnailUrl} alt="" />
                   </div>
                    <div className="bookCard_detail">
                    <h4 style={{color:"darkblue"}}>Title: {data.title}</h4> 
                    <h6>Authors: {data.authors.toString()}</h6>
                    <h6>Pagecount: {data.pageCount}</h6>
                    <button onClick={()=>handleDelete(data.id,data.title)}>Delete</button>
                    </div>
                   </div> 
                ))}
            </div>
        </div>
     );
}
 
export default BookList;
