import { useState } from 'react'

const Movies = () => {
    
    let favourites=[
        {
        title:"THE LITTLE THINGS",
        genre:"Love",
        language:"Hindi",
        rating:4.6,
        id:1
        },
        {
            title:"Stranger things",
            genre:"horror",
            language:"english",
            rating:4.5,
            id:2
        },
        {
            title:"Dark",
            genre:"Sci/mystry",
            language:"German",
            rating:4.7,
            id:3 
        },
        {
            title:"Family Man",
            genre:"thriller",
            language:"hindi",
            rating:4,
            id:4
        },
        {
            title:"Malgudi days",
            genre:"philosophy",
            language:"Hindi",
            rating:4.8,
            id:5
        }
    ]
    //dafault val arr favourites
    //change var name in htmln alse i.e favsData
    let [favouritesData,setFavsData]=useState(favourites)

    // pass id as an arg to the html button
    let handleRemove=(id,title)=>{
        // alert(`${id} ${title}`)
        setFavsData(favouritesData.filter(x=>x.id!=id))
        alert(`${title} has been removed from favourites`)
    }

    return ( 
        <div className="movies">
            <h1>Favourites of All Time</h1>
         
            <div className="fav">
                {favouritesData.map((data)=>(
                    <div className="fav_list">
                        <h2>{data.title}</h2>
                        {/* displaying 2 diff values */}
                        <p>{data.language}/{data.genre}</p> 
                        <p>{data.rating}â˜…</p>
                        <button onClick={()=>handleRemove(data.id,data.title)}>Remove</button>
                        
                    </div>
                ))}
               
            </div>
        </div>
     );
}
 
export default Movies;
