import { useState } from "react";
import { useEffect } from "react";

const BookList = () => {
     let [books,setBooks]=useState([])

     useEffect(()=>{
        let fetchData= async()=>{
            let response= await fetch('http://localhost:4001/books')
            let data= await response.json()
            setBooks(data)
        }
        fetchData()
     },[])
     let handleDelete=(id,title)=>{
        setBooks(books.filter(x=>x.id !== id))
        alert(`${title} has been deleted`)
     }


    return ( 
        <div className="booklist">
            <h1>Book List</h1>
            <div className="booksection">
                {books.map=((data)=>(
                 <div className="book">
                    <h4>{data.title}</h4>
                    <h6>Authors: {data.authors.toString()}</h6>
                    <h6>Catagory:{data.catagory.toString()}</h6>
                    <h6>PageCount:{data.pageCount}</h6>
                    <button onClick={()=>handleDelete(data.id,data.title)}></button>
                 </div>
                ))}

            </div>
        </div>

     );
}
 
export default BookList;
